import React, { useState } from "react";
import { useLocation } from "react-router-dom"; // Import useLocation hook

const PaymentPage = () => {
  const { state } = useLocation(); // Get the state passed from CartPage
  const { cart = [], totalPrice = 0 } = state || {}; // Destructure cart and totalPrice with defaults
  const [paymentStatus, setPaymentStatus] = useState(null);

  // Handle payment logic
  const handlePayment = () => {
    // Simulate a payment process
    setPaymentStatus("Payment Successful!");
    // Integrate with a payment gateway like Stripe, PayPal, etc.
  };

  return (
    <div style={styles.paymentContainer}>
      <h1 style={styles.paymentHeading}>Payment Details</h1>
      <div style={styles.cartDetails}>
        <h2>Cart Items</h2>
        {cart.length === 0 ? (
          <p>No items in the cart</p>
        ) : (
          cart.map((item) => (
            <div key={item._id} style={styles.cartItem}>
              <img src={item.image} alt={item.name} style={styles.cartImage} />
              <div style={styles.cartItemInfo}>
                <p style={styles.cartItemName}>{item.name}</p>
                <p style={styles.cartItemPrice}>₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
                <p style={styles.cartItemTotal}>Total: ₹{item.price * item.quantity}</p>
              </div>
            </div>
          ))
        )}
        <div style={styles.cartSummary}>
          <h3>Total: ₹{totalPrice}</h3>
        </div>
      </div>
      <button onClick={handlePayment} style={styles.paymentButton}>
        Pay Now
      </button>
      {paymentStatus && <p style={styles.paymentStatus}>{paymentStatus}</p>}
    </div>
  );
};

const styles = {
  paymentContainer: {
    padding: "30px",
    backgroundColor: "#f7f7f7",
    borderRadius: "12px",
    maxWidth: "800px",
    margin: "0 auto",
    marginTop: "70px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  },
  paymentHeading: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
    textTransform: "uppercase",
  },
  cartDetails: {
    display: "flex",
    flexDirection: "column",
    gap: "25px",
  },
  cartItem: {
    display: "flex",
    alignItems: "center",
    padding: "20px",
    backgroundColor: "#fff",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    marginBottom: "15px",
  },
  cartImage: {
    width: "100px",
    height: "100px",
    objectFit: "cover",
    borderRadius: "10px",
    marginRight: "15px",
  },
  cartItemInfo: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    gap: "8px",
  },
  cartItemName: {
    fontSize: "18px",
    fontWeight: "bold",
  },
  cartItemPrice: {
    fontSize: "16px",
    color: "#666",
  },
  cartItemTotal: {
    fontSize: "18px",
    fontWeight: "bold",
  },
  cartSummary: {
    marginTop: "20px",
    fontSize: "20px",
    fontWeight: "bold",
  },
  paymentButton: {
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    padding: "15px 25px",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "18px",
    width: "100%",
    marginTop: "20px",
  },
  paymentStatus: {
    marginTop: "20px",
    fontSize: "18px",
    color: "#4caf50",
    textAlign: "center",
  },
};

export default PaymentPage;
