import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserProfile() {
  const [userData, setUserData] = useState(null); // For user data
  const [cartData, setCartData] = useState([]); // For cart data

  useEffect(() => {
    const fetchData = async () => {
      try {
        // Step 1: Get user data
        const userResponse = await axios.get('/api/user/me'); // Assuming your API route is /api/user/me
        setUserData(userResponse.data);
        
        // Step 2: Get user's cart data
        const cartResponse = await axios.get('/api/cart'); // Assuming /api/cart gets the cart for the authenticated user
        setCartData(cartResponse.data.items);
      } catch (error) {
        console.error('❌ Error fetching user/cart data:', error);
      }
    };

    fetchData();
  }, []);

  if (!userData) {
    return <p>Loading user data...</p>;
  }

  return (
    <div>
      <h1>Welcome, {userData?.name || 'Guest'}!</h1>
      <p>Email: {userData?.email || 'No email available'}</p>

      <h2>Your Cart</h2>
      {cartData.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <ul>
          {cartData.map((item, index) => (
            <li key={index}>
              <h3>{item.productName}</h3>
              <p>{item.productDescription}</p>
              <p>Price: ${item.productPrice}</p>
              <p>Quantity: {item.quantity}</p>
              <p>Total: ${item.totalPrice}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default UserProfile;
