import React, { useState } from "react";
import axios from "axios"; // You can use axios to make the API call

const PaymentPage = () => {
  const [paymentStatus, setPaymentStatus] = useState(null);
  const [amount, setAmount] = useState(0);  // Amount to be paid/earned by artisan
  const [artisianId, setArtisianId] = useState("");  // Artisan ID
  
  // Function to handle payment and update artisan's earned amount
  const handlePayment = async () => {
    setPaymentStatus("Processing...");
    alert("Confirm payment");

    try {
      // Simulating the payment process (replace with actual payment gateway API)
      setTimeout(async () => {
        setPaymentStatus("Payment Successful!");

        // After successful payment, update the artisan's earned amount
        if (amount > 0 && artisianId) {
          try {
            const response = await axios.post("/api/update-earned-amount", {
              artisianId,
              amount
            });

            // Handle the response from the backend
            if (response.status === 200) {
              alert("Earned amount updated for the artisan!");
            }
          } catch (error) {
            console.error("Error updating earned amount:", error);
            alert("Failed to update earned amount. Please try again.");
          }
        }
      }, 3000);
    } catch (error) {
      setPaymentStatus("Payment Failed. Please try again.");
      console.error("Payment error:", error);
    }
  };

  return (
    <div style={styles.paymentContainer}>
      <h1 style={styles.paymentHeading}>Payment Details</h1>

      {/* Input fields to simulate payment */}
      <div style={styles.paymentForm}>
        <label style={styles.label}>Artisan ID</label>
        <input
          type="text"
          value={artisianId}
          onChange={(e) => setArtisianId(e.target.value)}
          placeholder="Enter Artisan ID"
          style={styles.inputField}
        />
        <label style={styles.label}>Amount</label>
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          placeholder="Enter payment amount"
          style={styles.inputField}
        />
      </div>

      {/* Payment button */}
      <button onClick={handlePayment} style={styles.paymentButton}>
        Pay Now
      </button>
      {paymentStatus && <p style={styles.paymentStatus}>{paymentStatus}</p>}
    </div>
  );
};

const styles = {
  paymentContainer: {
    padding: "40px",
    backgroundColor: "#f4f4f4",
    borderRadius: "12px",
    maxWidth: "700px",
    margin: "0 auto",
    marginTop: "70px",
    boxShadow: "0 4px 20px rgba(0, 0, 0, 0.2)",
  },
  paymentHeading: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
    textTransform: "uppercase",
  },
  paymentForm: {
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    marginBottom: "30px",
  },
  label: {
    fontSize: "16px",
    color: "#333",
    marginBottom: "6px",
    fontWeight: "bold",
  },
  inputField: {
    width: "100%",
    padding: "12px",
    marginBottom: "15px",
    borderRadius: "8px",
    border: "1px solid #ddd",
    fontSize: "16px",
  },
  paymentButton: {
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    padding: "15px 25px",
    borderRadius: "4px",
    cursor: "pointer",
    fontSize: "18px",
    width: "100%",
    marginTop: "20px",
  },
  paymentStatus: {
    marginTop: "20px",
    fontSize: "18px",
    color: "#4caf50",
    textAlign: "center",
  },
};

export default PaymentPage;
