import React, { useEffect, useState } from 'react';

function UserProfile({ user }) {
  // State to hold the user data if it is being fetched from an API
  const [userData, setUserData] = useState(user || null); // Default user is null or the prop 'user'

  // Simulating an API call to fetch user data
  useEffect(() => {
    if (!user) {
      // Simulate API call (for demo purposes)
      setTimeout(() => {
        setUserData({
          name: 'John Doe',
          email: 'john.doe@example.com',
        });
      }, 2000); // Simulate 2-second delay
    }
  }, [user]);

  // If userData is still undefined or null, show a loading message
  if (!userData) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>Welcome, {userData?.name || 'Guest'}!</h1>
      <p>Email: {userData?.email || 'No email available'}</p>
    </div>
  );
}

// Default export for use in other parts of the app
export default UserProfile;
