import React, { useState } from "react";

const PaymentPage = ({ cart = [], totalPrice }) => {
  const [paymentStatus, setPaymentStatus] = useState(null);

  const handlePayment = () => {
    // Simulate a payment process
    setPaymentStatus("Payment Successful!");
    // Normally, here you would integrate with a payment gateway (like Stripe, PayPal, etc.)
    // For now, we are simulating a successful payment.
  };

  return (
    <div style={styles.paymentContainer}>
      <h1 style={styles.paymentHeading}>Payment Details</h1>
      <div style={styles.cartDetails}>
        <h2>Cart Items</h2>
        {cart.length === 0 ? (
          <p>No items in the cart</p>
        ) : (
          cart.map((item) => (
            <div key={item._id} style={styles.cartItem}>
              <img
                src={item.image}
                alt={item.name}
                style={styles.cartImage}
              />
              <div style={styles.cartItemInfo}>
                <p style={styles.cartItemName}>{item.name}</p>
                <p style={styles.cartItemPrice}>₹{item.price}</p>
                <p>Quantity: {item.quantity}</p>
                <p style={styles.cartItemTotal}>Total: ₹{item.price * item.quantity}</p>
              </div>
            </div>
          ))
        )}
        <div style={styles.cartSummary}>
          <h3>Total: ₹{totalPrice}</h3>
        </div>
      </div>
      <button onClick={handlePayment} style={styles.paymentButton}>
        Pay Now
      </button>
      {paymentStatus && <p style={styles.paymentStatus}>{paymentStatus}</p>}
    </div>
  );
};

const styles = {
  paymentContainer: {
    padding: "30px",
    backgroundColor: "#f7f7f7",
    borderRadius: "12px",
    maxWidth: "800px",
    margin: "0 auto",
    marginTop: "70px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  },
  paymentHeading: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
    textTransform: "uppercase",
  },
  cartDetails: {
    display: "flex",
    flexDirection: "column",
    gap: "25px",
  },
  cartItem: {
    display: "flex",
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    alignItems: "center",
    gap: "20px",
  },
  cartImage: {
    width: "120px",
    height: "120px",
    objectFit: "cover",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
  },
  cartItemInfo: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  cartItemName: {
    fontSize: "20px",
    fontWeight: "bold",
    color: "#333",
  },
  cartItemPrice: {
    fontSize: "18px",
    color: "#444",
    fontWeight: "bold",
  },
  cartItemTotal: {
    fontSize: "18px",
    color: "#333",
    fontWeight: "bold",
  },
  cartSummary: {
    marginTop: "20px",
    fontSize: "22px",
    fontWeight: "bold",
    textAlign: "center",
  },
  paymentButton: {
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    padding: "12px 25px",
    fontSize: "18px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  paymentStatus: {
    textAlign: "center",
    fontSize: "18px",
    color: "#28a745",
    marginTop: "20px",
  },
};

export default PaymentPage;
