import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; // Importing the CSS file

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const toggleMenu = () => setIsMobileMenuOpen(!isMobileMenuOpen);

  // Get the user's role from localStorage
  const role = localStorage.getItem("role"); // Can be 'user' or 'admin'

  // Logout function
  const logout = () => {
    localStorage.removeItem("role");
    localStorage.removeItem("token");
    window.location.href = '/'; // Redirect to home page
  };

  // Links for normal users
  const userLinks = (
    <>
      <li>
        <Link to="/" onClick={() => setIsMobileMenuOpen(false)}>Home</Link>
      </li>
      <li>
        <Link to="/productsList" onClick={() => setIsMobileMenuOpen(false)}>Products</Link>
      </li>
      <li>
        <Link to="/user-register" onClick={() => setIsMobileMenuOpen(false)}>User</Link>
      </li>
      <li>
        <Link to="/contact" onClick={() => setIsMobileMenuOpen(false)}>Contact</Link>
      </li>
      <li>
        <Link to="/cart" onClick={() => setIsMobileMenuOpen(false)}>Cart <i className="fas fa-shopping-cart"></i></Link>
      </li>
      <li>
        <Link to="/artisian-login" onClick={() => setIsMobileMenuOpen(false)}>Admin</Link>
      </li>
    </>
  );

  // Links for admins
  const adminLinks = (
    <>
      <li>
        <Link to="/dashboard" onClick={() => setIsMobileMenuOpen(false)}>Dashboard</Link>
      </li>
      <li>
        <button className="logout-button" onClick={logout}>Logout</button>
      </li>
    </>
  );

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">HandmadeHub</Link>
      </div>

      <div className="menu-icon" onClick={toggleMenu}>
        <i className={isMobileMenuOpen ? "fas fa-times" : "fas fa-bars"}></i>
      </div>

      <ul className={isMobileMenuOpen ? "nav-links active" : "nav-links"}>
        {/* Show user links only if not an admin */}
        {role === "admin" ? adminLinks : userLinks}
      </ul>
    </nav>
  );
};

export default Navbar;
