import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserProfile() {
  const [userData, setUserData] = useState(null); // State to store user data
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await axios.get('http://localhost:4000/api/userprofile', {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${localStorage.getItem('token')}` // Add JWT token if required
          }
        });
        setUserData(response.data); // Set user data from API response
      } catch (err) {
        console.error('Error fetching user profile:', err);
        setError('Failed to load user profile. Please try again later.');
      } finally {
        setLoading(false); // Set loading to false after API call is complete
      }
    };

    fetchUserData();
  }, []);

  if (loading) return <p>Loading...</p>; // Loading state

  if (error) return <p>{error}</p>; // Error state

  return (
    <div>
      <h1>Welcome, {userData?.name || 'Guest'}!</h1>
      <p><strong>Email:</strong> {userData?.email || 'No email available'}</p>
    </div>
  );
}

export default UserProfile;
