import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Navbar.css"; // Importing the CSS file

const Navbar = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Toggle for mobile menu
  const toggleMenu = () => setIsMobileMenuOpen(!isMobileMenuOpen);

  // Check if admin is logged in
  const isAdmin = localStorage.getItem("admin");
  // If the user is an admin, don't show the navbar

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">HandmadeHub</Link>
      </div>

      <div className="menu-icon" onClick={toggleMenu}>
        <i className={isMobileMenuOpen ? "fas fa-times" : "fas fa-bars"}></i>
      </div>

      <ul className={isMobileMenuOpen ? "nav-links active" : "nav-links"}>
        <li>
          {isAdmin ? null : ( // If the user is an admin, don't show the home link
            <Link to="/" onClick={() => setIsMobileMenuOpen(false)}>
              Home
            </Link>
          )}
        </li>
        <li>
          {isAdmin ? null : ( // If the user is an admin, don't show the products link
            <Link to="/productsList" onClick={() => setIsMobileMenuOpen(false)}>
              Products
            </Link>
          )}
        </li>
        <li>
          {
            isAdmin ? null : (
              <Link to="/user-register" onClick={() => setIsMobileMenuOpen(false)}>
                User
              </Link>
            )
          }
          {/* <Link to="/user-register" onClick={() => setIsMobileMenuOpen(false)}>
            User
          </Link> */}
        </li>
        <li>
          {isAdmin ? null : (
            <Link to="/contact" onClick={() => setIsMobileMenuOpen(false)}>
              Contact
            </Link>
          )}
        </li>
        <li>
          {isAdmin ? null : ( // If the user is an admin, don't show the cart icon
            <Link to="/cart" onClick={() => setIsMobileMenuOpen(false)}>
              Cart <i className="fas fa-shopping-cart"></i>
            </Link>
          )}
          <Link
            to="/cart"
            className="cart-icon"
            onClick={() => setIsMobileMenuOpen(false)}
          ></Link>
        </li>
        <li>
          {isAdmin? (
            <button onClick={() => localStorage.removeItem("admin","token")}>Logout</button>
          ) : null}
        </li>
        <li>
          {isAdmin ? ( // If the user is an admin, don't show the login/register link
            <Link to="/dashboard" onClick={() => setIsMobileMenuOpen(false)}>
              Hello Admin!
            </Link>
          ) : (
            <Link to="/artisian-login" onClick={() => setIsMobileMenuOpen(false)}>
              Admin            </Link>
          )}
        </li>
      </ul>
    </nav>
  );
};

export default Navbar;
