import React from 'react';

// Reusable Button Component
const Button = ({ href, children }) => (
  <a 
    href={href} 
    style={{ 
      padding: '15px 40px', 
      fontSize: '1.2rem', 
      backgroundColor: '#FF5722', 
      color: '#fff', 
      borderRadius: '25px', 
      textDecoration: 'none', 
      transition: 'all 0.3s ease', 
      display: 'inline-block' 
    }}
    onMouseOver={(e) => e.target.style.backgroundColor = '#E64A19'}
    onMouseOut={(e) => e.target.style.backgroundColor = '#FF5722'}
  >
    {children}
  </a>
);

// Reusable Product Card Component
const ProductCard = ({ imageSrc, title, description }) => (
  <div style={{ 
    width: '280px', 
    borderRadius: '15px', 
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)', 
    overflow: 'hidden', 
    backgroundColor: '#fff', 
    transition: 'transform 0.3s' 
  }}
  onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
  onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
  >
    <img src={imageSrc} alt={title} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h3 style={{ fontSize: '1.4rem', marginBottom: '10px' }}>{title}</h3>
      <p style={{ color: '#555' }}>{description}</p>
    </div>
  </div>
);

// Hero Section
const Hero = () => (
  <section 
    style={{ 
      backgroundImage: 'url(https://source.unsplash.com/1600x900/?handicrafts,artisans)', 
      backgroundSize: 'cover', 
      backgroundPosition: 'center', 
      height: '600px', 
      color: '#fff', 
      textAlign: 'center', 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      padding: '20px', 
      position: 'relative'
    }}
  >
    <div style={{ backgroundColor: 'rgba(0, 0, 0, 0.6)', padding: '40px', borderRadius: '15px' }}>
      <h1 style={{ fontSize: '3.5rem', fontWeight: 'bold' }}>Support Artisans, Embrace Tradition</h1>
      <p style={{ fontSize: '1.5rem', margin: '20px 0' }}>
        Discover authentic handmade textiles and handicrafts from Indiaâ€™s finest artisans.
      </p>
      <Button href="/products">Shop Now</Button>
    </div>
  </section>
);

// About Section
const About = () => (
  <section style={{ textAlign: 'center', padding: '80px 20px', backgroundColor: '#f9f9f9' }}>
    <h2 style={{ fontSize: '2.8rem', marginBottom: '20px' }}>Empowering Rural Artisans</h2>
    <p style={{ fontSize: '1.2rem', lineHeight: '1.8', maxWidth: '800px', margin: '0 auto', color: '#555' }}>
      Textiles and handicrafts form a major pillar of the rural economy. Our mission is to bring the talent of India's artisans to the global stage. 
      Artisans can register, upload their products, showcase their skills, and organize self-created events. Public users can browse and purchase unique, one-of-a-kind products.
    </p>
  </section>
);

// Featured Products Section
const FeaturedProducts = () => (
  <section style={{ padding: '80px 20px', backgroundColor: '#fff' }}>
    <h2 style={{ fontSize: '2.8rem', textAlign: 'center', marginBottom: '40px' }}>Featured Products</h2>
    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', gap: '30px' }}>
      {['Handwoven Saree', 'Intricate Embroidery Kurti', 'Handmade Pottery'].map((product, index) => (
        <ProductCard 
          key={index} 
          imageSrc="https://source.unsplash.com/300x300/?handicrafts" 
          title={product} 
          description="Explore the latest creations from skilled artisans." 
        />
      ))}
    </div>
  </section>
);

// Call-to-Action Section for Artisan Registration
const ArtisanCTA = () => (
  <section style={{ textAlign: 'center', padding: '80px 20px', backgroundColor: '#4A90E2', color: '#fff' }}>
    <h2 style={{ fontSize: '2.8rem', marginBottom: '20px' }}>Are You an Artisan?</h2>
    <p style={{ fontSize: '1.2rem', marginBottom: '20px' }}>
      Join our platform to showcase your handcrafted products, track your earnings, and participate in global exhibitions.
    </p>
    <Button href="/register">Register as an Artisan</Button>
  </section>
);

// Footer Section
const Footer = () => (
  <footer style={{ backgroundColor: '#333', color: '#fff', textAlign: 'center', padding: '40px 0' }}>
    <p>&copy; 2024 HandmadeHub. All Rights Reserved.</p>
  </footer>
);

// Home Page Component
const Home = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9', color: '#333' }}>
      <Hero />
      <About />
      <FeaturedProducts />
      <ArtisanCTA />
      <Footer />
    </div>
  );
};

export default Home;
