import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const history = useNavigate();  // Initialize useHistory hook

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity < 1) return;

    const updatedCart = cart.map((item) =>
      item._id === id ? { ...item, quantity: newQuantity } : item
    );
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleRemoveItem = (id) => {
    const updatedCart = cart.filter((item) => item._id !== id);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const proceedToBuy = () => {
    const totalPrice = calculateTotal();
    history.push("/payment", { cart, totalPrice });  // Pass cart and totalPrice to PaymentPage
  };

  if (cart.length === 0) {
    return <div style={styles.emptyCartMessage}>Your cart is empty.</div>;
  }

  return (
    <div style={styles.cartContainer}>
      <h1 style={styles.heading}>Your Shopping Cart</h1>
      <div style={styles.cartItems}>
        {cart.map((item) => (
          <div key={item._id} style={styles.cartItem}>
            <img
              src={item.image}
              alt={item.name}
              style={styles.cartImage}
            />
            <div style={styles.cartDetails}>
              <p style={styles.cartItemName}>{item.name}</p>
              <p style={styles.cartItemArtisan}>
                Artisan: {item.artisanId ? item.artisanId.name : "Unknown"}
              </p>
              <p style={styles.cartItemPrice}>₹{item.price}</p>
              <div style={styles.quantity}>
                <label htmlFor={`quantity-${item._id}`} style={styles.label}>
                  Quantity:
                </label>
                <input
                  type="number"
                  id={`quantity-${item._id}`}
                  value={item.quantity}
                  min="1"
                  onChange={(e) =>
                    handleQuantityChange(item._id, Number(e.target.value))
                  }
                  style={styles.input}
                />
              </div>
              <p style={styles.cartItemTotal}>
                Total: ₹{item.price * item.quantity}
              </p>
            </div>
            <button
              onClick={() => handleRemoveItem(item._id)}
              style={styles.removeButton}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
      <div style={styles.cartSummary}>
        <h2 style={styles.summaryHeading}>Cart Summary</h2>
        <p style={styles.summaryText}>
          Total Price: ₹{calculateTotal()}
        </p>
        <button style={styles.checkoutBtn} onClick={proceedToBuy}>
          Proceed to Buy
        </button>
      </div>
    </div>
  );
};
const styles = {
  container: {
    // your styles here
  },
};


export default CartPage;
