import React from 'react';

// Reusable Button Component
const Button = ({ href, children }) => (
  <a 
    href={href} 
    style={{ 
      display: 'inline-block', 
      padding: '15px 40px', 
      fontSize: '1.2rem', 
      background: 'linear-gradient(90deg, #FF5733, #C70039)', 
      color: '#fff', 
      border: 'none', 
      borderRadius: '30px', 
      textDecoration: 'none', 
      cursor: 'pointer', 
      transition: 'all 0.3s ease-in-out', 
      boxShadow: '0 10px 20px rgba(0,0,0,0.2)' 
    }}
    onMouseEnter={(e) => e.target.style.transform = 'scale(1.05)'}
    onMouseLeave={(e) => e.target.style.transform = 'scale(1)'}
  >
    {children}
  </a>
);

// Reusable Product Card Component
const ProductCard = ({ imageSrc, title, description }) => (
  <div style={{ 
    width: '250px', 
    borderRadius: '15px', 
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)', 
    overflow: 'hidden', 
    transition: 'transform 0.3s', 
    backgroundColor: '#fff' 
  }}
    onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
    onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
  >
    <img src={imageSrc} alt={title} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h3 style={{ fontSize: '1.2rem', fontWeight: 'bold' }}>{title}</h3>
      <p style={{ fontSize: '1rem', color: '#666' }}>{description}</p>
    </div>
  </div>
);

// Reusable Event Card Component
const EventCard = ({ imageSrc, title, description }) => (
  <div style={{ 
    width: '300px', 
    borderRadius: '15px', 
    boxShadow: '0 8px 16px rgba(0,0,0,0.1)', 
    overflow: 'hidden', 
    transition: 'transform 0.3s', 
    backgroundColor: '#fff' 
  }}
    onMouseEnter={(e) => e.currentTarget.style.transform = 'scale(1.05)'}
    onMouseLeave={(e) => e.currentTarget.style.transform = 'scale(1)'}
  >
    <img src={imageSrc} alt={title} style={{ width: '100%', height: '200px', objectFit: 'cover' }} />
    <div style={{ padding: '20px', textAlign: 'center' }}>
      <h3 style={{ fontSize: '1.2rem', fontWeight: 'bold' }}>{title}</h3>
      <p style={{ fontSize: '1rem', color: '#666' }}>{description}</p>
    </div>
  </div>
);

// Hero Section
const Hero = () => (
  <section 
    style={{ 
      backgroundImage: 'url(https://www.vintagemillwerks.com/wp-content/uploads/2020/11/tSRJI8bdqmIKmXFGS8xUohNL95netAJO1606168370.jpg)', 
      backgroundSize: 'cover', 
      backgroundPosition: 'center', 
      height: '600px', 
      color: '#fff', 
      textAlign: 'center', 
      display: 'flex', 
      flexDirection: 'column', 
      justifyContent: 'center', 
      alignItems: 'center', 
      padding: '20px' 
    }}
  >
    <h1 style={{ fontSize: '4rem', fontWeight: 'bold', textShadow: '2px 2px 5px rgba(0,0,0,0.5)' }}>Support Artisans, Embrace Tradition</h1>
    <p style={{ fontSize: '1.5rem', margin: '20px 0', maxWidth: '800px' }}>
      Discover authentic handmade textiles and handicrafts from Indiaâ€™s finest artisans.
    </p>
    <Button href="/products">Shop Now</Button>
  </section>
);

// About Section
const About = () => (
  <section style={{ textAlign: 'center', padding: '50px', backgroundColor: '#f4f4f9' }}>
    <h2 style={{ fontSize: '2.5rem', marginBottom: '20px' }}>Empowering Rural Artisans</h2>
    <p style={{ fontSize: '1.2rem', lineHeight: '1.6', maxWidth: '800px', margin: '0 auto' }}>
      Our mission is to bring the talent of India's artisans to the global stage. Artisans can register, upload products, and organize self-created events.
    </p>
  </section>
);

// Featured Products Section
const FeaturedProducts = () => (
  <section style={{ padding: '50px', backgroundColor: '#fff' }}>
    <h2 style={{ fontSize: '2.5rem', textAlign: 'center', marginBottom: '20px' }}>Featured Products</h2>
    <div style={{ display: 'flex', justifyContent: 'center', gap: '20px', flexWrap: 'wrap' }}>
      {['Handwoven Saree', 'Intricate Embroidery Kurti', 'Handmade Pottery'].map((product, index) => (
        <ProductCard 
          key={index} 
          imageSrc="https://th.bing.com/th/id/OIP.hkvha8UCZbaFSsx2YkPBmAHaFS?w=266&h=191&c=7&r=0&o=5&dpr=1.3&pid=1.7" 
          title={product} 
          description="Explore the latest creations from skilled artisans." 
        />
      ))}
    </div>
  </section>
);

const Footer = () => (
  <footer style={{ backgroundColor: '#333', color: '#fff', textAlign: 'center', padding: '20px 0' }}>
    <p>&copy; 2024 HandmadeHub. All Rights Reserved.</p>
  </footer>
);

const Home = () => {
  return (
    <div style={{ fontFamily: 'Arial, sans-serif', backgroundColor: '#f4f4f9', color: '#333' }}>
      <Hero />
      <About />
      <FeaturedProducts />
      <Footer />
    </div>
  );
};

export default Home;
