import React, { useState, useEffect } from "react";

const CartPage = () => {
  // State to manage the cart
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
    storedCart.forEach(item => console.log(item.artisanId.name));  // Correctly access artisanId for each item
  }, []);

  // Function to calculate the total price
  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  // Function to handle quantity change
  const handleQuantityChange = (id, newQuantity) => {
    if (newQuantity < 1) return; // Prevent negative or zero quantity

    const updatedCart = cart.map((item) =>
      item._id === id ? { ...item, quantity: newQuantity } : item
    );
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // Save updated cart to localStorage
  };

  // Function to handle removal of an item from the cart
  const handleRemoveItem = (id) => {
    const updatedCart = cart.filter((item) => item._id !== id);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart)); // Save updated cart to localStorage
  };

  if (cart.length === 0) {
    return <div style={styles.emptyCartMessage}>Your cart is empty.</div>;
  }

  return (
    <div style={styles.cartContainer}>
      <h1 style={styles.heading}>Your Shopping Cart</h1>
      <div style={styles.cartItems}>
        {cart.map((item) => (
          <div key={item._id} style={styles.cartItem}>
            <img
              src={item.image}
              alt={item.name}
              style={styles.cartImage}
            />
            <div style={styles.cartDetails}>
              <p style={styles.cartItemName}>{item.name}</p>
              <p style={styles.cartItemArtisan}>
                Artisan: {item.artisanId ? item.artisanId.name : "Unknown"}
              </p>
              <p style={styles.cartItemPrice}>₹{item.price}</p>
              <div style={styles.quantity}>
                <label htmlFor={`quantity-${item._id}`} style={styles.label}>
                  Quantity:
                </label>
                <input
                  type="number"
                  id={`quantity-${item._id}`}
                  value={item.quantity}
                  min="1"
                  onChange={(e) =>
                    handleQuantityChange(item._id, Number(e.target.value))
                  }
                  style={styles.input}
                />
              </div>
              <p style={styles.cartItemTotal}>
                Total: ₹{item.price * item.quantity}
              </p>
            </div>
            <button
              onClick={() => handleRemoveItem(item._id)}
              style={styles.removeButton}
            >
              Remove
            </button>
          </div>
        ))}
      </div>
      <div style={styles.cartSummary}>
        <h2 style={styles.summaryHeading}>Cart Summary</h2>
        <p style={styles.summaryText}>
          Total Price: ₹{calculateTotal()}
        </p>
        <button style={styles.checkoutBtn}>Proceed to Buy</button>
      </div>
    </div>
  );
};

const styles = {
  cartContainer: {
    padding: "30px",
    backgroundColor: "#f7f7f7",
    borderRadius: "12px",
    maxWidth: "100%",
    margin: "0 auto",
    marginTop: "70px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
  },
  heading: {
    textAlign: "center",
    fontSize: "28px",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
    textTransform: "uppercase",
  },
  cartItems: {
    display: "flex",
    flexDirection: "column",
    gap: "25px",
  },
  cartItem: {
    display: "flex",
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    alignItems: "center",
    gap: "20px",
  },
  cartImage: {
    width: "120px",
    height: "120px",
    objectFit: "cover",
    borderRadius: "8px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.1)",
  },
  cartDetails: {
    flex: 1,
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  cartItemName: {
    fontSize: "20px",
    fontWeight: "bold",
    color: "#333",
  },
  cartItemArtisan: {
    fontSize: "16px",
    color: "#555",
  },
  cartItemPrice: {
    fontSize: "18px",
    color: "#444",
    fontWeight: "bold",
  },
  quantity: {
    display: "flex",
    alignItems: "center",
    gap: "10px",
  },
  label: {
    fontSize: "16px",
    color: "#555",
  },
  input: {
    width: "60px",
    padding: "8px",
    fontSize: "16px",
    border: "1px solid #ddd",
    borderRadius: "5px",
    textAlign: "center",
  },
  cartItemTotal: {
    fontSize: "18px",
    color: "#333",
    fontWeight: "bold",
  },
  removeButton: {
    backgroundColor: "#ff4d4d",
    color: "#fff",
    border: "none",
    padding: "10px 20px",
    borderRadius: "5px",
    cursor: "pointer",
    fontSize: "16px",
    transition: "background-color 0.3s",
  },
  removeButtonHover: {
    backgroundColor: "#ff1a1a",
  },
  cartSummary: {
    marginTop: "40px",
    backgroundColor: "#fff",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
  },
  summaryHeading: {
    fontSize: "24px",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  summaryText: {
    fontSize: "22px",
    fontWeight: "600",
    marginBottom: "25px",
  },
  checkoutBtn: {
    backgroundColor: "#28a745",
    color: "#fff",
    border: "none",
    padding: "12px 25px",
    fontSize: "18px",
    borderRadius: "5px",
    cursor: "pointer",
    transition: "background-color 0.3s",
  },
  checkoutBtnHover: {
    backgroundColor: "#218838",
  },
  emptyCartMessage: {
    textAlign: "center",
    fontSize: "22px",
    fontWeight: "bold",
    color: "#777",
  },
};

export default CartPage;
