import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Sales = () => {
  const artistId = localStorage.getItem('artid'); // Ensure 'artid' is set properly during login

  const [salesData, setSalesData] = useState([]);
  const [salesSummary, setSalesSummary] = useState({
    sales: 0,
    profit: 0
  });

  useEffect(() => {
    const fetchSalesData = async () => {
      try {
        // Make requests to both APIs simultaneously
        const [productResponse, salesResponse] = await Promise.all([
          axios.get(`http://localhost:4000/api/productAdmin/${artistId}`),
          axios.get(`http://localhost:4000/api/salesSummary/${artistId}`)
        ]);

        // Set the product data (for sales table)
        setSalesData(productResponse.data);

        // Set the sales summary (total sales and profit)
        setSalesSummary(salesResponse.data);
      } catch (error) {
        console.error('Error fetching data from APIs:', error);
      }
    };

    fetchSalesData();
  }, [artistId]);

  const tableHeaderStyle = {
    backgroundColor: '#4A90E2',
    color: '#fff',
    padding: '10px',
    textAlign: 'left',
  };

  const tableCellStyle = {
    padding: '10px',
    textAlign: 'left',
    borderBottom: '1px solid #ccc',
  };

  return (
    <div>
      <h2>Sales & Profit Data</h2>
      <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr>
            <th style={tableHeaderStyle}>Products</th>
          </tr>
        </thead>
        <tbody>
          {salesData.map((data, index) => (
            <tr key={index}>
              <td style={tableCellStyle}>{data.name}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <h1>Total Sales: ₹{salesSummary.sales}</h1>
      <h2>Total Profit: ₹{salesSummary.profit}</h2>
    </div>
  );
};

export default Sales;
