import React, { useEffect, useState } from 'react';
import axios from 'axios';

function UserProfile() {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token'); // Get JWT from localStorage (optional)
        const response = await axios.get('http://localhost:4000/api/userprofile', {
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${token}`, // Include token if needed
          }
        });
        setUserData(response.data.user || response.data); // Update if response structure has a "user" field
      } catch (err) {
        console.error('Error fetching user profile:', err);
        setError(err.response?.data?.message || 'Failed to load user profile. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, []);

  if (loading) return <p>Loading...</p>;

  if (error) return <p style={{ color: 'red' }}>{error}</p>;

  if (!userData) return <p>No user profile found</p>;

  return (
    <div>
      <h1>Welcome, {userData.name || 'Guest'}!</h1>
      <p><strong>Email:</strong> {userData.email || 'No email available'}</p>
    </div>
  );
}

export default UserProfile;
